    "plt.scatter(filtered_df['start_station_longitude'], filtered_df['start_station_latitude'], s=0.01)\n",
    "plt.ylim(40.64,40.83)\n",
    "plt.xlim(-74.03,-73.9)\n",
    "plt.show()"
